INSERT INTO sample (id




                  ,name





                  ,test_id




                   ,reg_id
                   ,reg_dt
                   ,mod_id
                   ,mod_dt



                   )
VALUES (#{id}




       ,#{name}





       ,#{test_id}




       ,#{reg_id}
       ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
       ,#{mod_id}
       ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'))

SELECT NVL (a.id, b.id) AS id FROM (SELECT MAX (id) AS id FROM sample WHERE id = #{id}) a, (SELECT NVL (MAX (id) + 1, 1) AS id FROM sample) b
MERGE INTO sample USING DUAL ON (id = #{id}) WHEN NOT MATCHED THEN INSERT (id
,name
,test_id
,reg_id ,reg_dt ,mod_id ,mod_dt
) VALUES (#{id}
,#{name}
,#{test_id}
,#{reg_id} ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') ,#{mod_id} ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')) WHEN MATCHED THEN UPDATE
name = #{name},
test_id = #{test_id},
,mod_id = #{mod_id}, mod_dt = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') WHERE id = #{id}
