mapper.select:


SELECT *
  FROM (




SELECT sample_id
      ,sample_name
      ,test_id
      ,reg_id
      ,reg_dt
      ,mod_id
      ,mod_dt




      ,ROW_NUMBER() OVER (ORDER BY ${order_by}) rnum




  FROM sample
 WHERE 1=1




   AND sample_id = #{sample_id}





   AND sample_name = #{sample_name}





   AND test_id = #{test_id}





ORDER BY ${order_by}





) WHERE rnum BETWEEN #{rows_per_page} * (#{page_number} - 1) + 1 AND #{rows_per_page} * #{page_number}
ORDER BY rnum

mapper.insert:


SELECT sample_seq.NEXTVAL AS sample_id
  FROM dual
/*SELECT NVL (MAX (sample_id) + 1, 1) AS sample_id
  FROM sample*/




INSERT INTO sample (sample_id
                   ,sample_name
                   ,test_id
                   ,reg_id
                   ,reg_dt
                   ,mod_id
                   ,mod_dt)
VALUES (#{sample_id}
       ,#{sample_name}
       ,#{test_id}
       ,#{reg_id}
       ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
       ,#{mod_id}
       ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'))

mapper.update:

UPDATE sample
   SET sample_name = #{sample_name}
      ,test_id = #{test_id}
      ,mod_id = #{mod_id}
      ,mod_dt = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE sample_id = #{sample_id}
