mapper.select:
	

	
SELECT
	

	
	
 ${hint}
	
	

	
	
 reg_id reg_id,reg_dt reg_dt,mod_id mod_id,mod_dt mod_dt,id id,name name,test_id test_id
	
	
	
	
 ${fields}
	
	

	

	
  FROM sample
	

	
	
	
   AND ${condition}
	
	
	
	
   AND reg_id = #{reg_id}
	
	
	
	
   AND reg_dt = #{reg_dt}
	
	
	
	
   AND mod_id = #{mod_id}
	
	
	
	
   AND mod_dt = #{mod_dt}
	
	
	
	
   AND id = #{id}
	
	
	
	
   AND name = #{name}
	
	
	
	
   AND test_id = #{test_id}
	
	
	

	
	
HAVING ${having}
	
	

	
	
GROUP BY ${group_by}
	
	

	
	
ORDER BY ${order_by}

		

		
  FROM sample
		

	
	
	
   AND ${condition}
	
	
	
	
   AND reg_id = #{reg_id}
	
	
	
	
   AND reg_dt = #{reg_dt}
	
	
	
	
   AND mod_id = #{mod_id}
	
	
	
	
   AND mod_dt = #{mod_dt}
	
	
	
	
   AND id = #{id}
	
	
	
	
   AND name = #{name}
	
	
	
	
   AND test_id = #{test_id}
