mapper.update:
		UPDATE 

		SET
 			upd_date       = now()
 			,editor_id     = #{editor_id}
			,name          = #{name}
			,minor         = #{minor}
			,signal_period = #{signal_period}
			,txpower       = #{txpower}
			,description   = #{description}
			,zone_uid      = #{zone_uid}
		WHERE
			beacon_uid = #{beacon_uid}
		LIMIT 1
